clear clc n=(0:127); x=zeros(1,128); x(1:64)=3*sin((n(1:64)*pi)/8)+3; figure(1) stem(x) title('x[n]') X=fft(x); figure(2) stem(n,X) title('X[k]') z=7*cos((n(1,1:64)*pi)/16).*cos(n(1,1:64)*pi); z(65:128)=zeros(1,64); figure(3) stem(z) title('z[n]') Z=fft(z); figure(4) stem(n,Z) title('Z[K]') w=x+z; figure(5) stem(w) title('w[n]') W=fft(w); figure(6) stem(n,W) title('W[K]') figure(7) stem(n,W) hold on stem(X) hold on stem(Z) hold off title ('w[K]&X[K]&Z[K]') h=zeros(1,128); h(1:64) = 0.5*sinc(0.5*(n(1:64) - 31)); figure(8) stem(h) title('h[n]') H=fft(h); figure(9) stem(n,H) title('H[K]') figure(10) stem(abs(H)) title('magnitude of H[K]') figure(11) stem(angle(H)) 
title('phase of H[K]') y=ifft(W.*H); figure(12) stem(n,y) title('y[n]') figure(13) stem(x) hold on stem(n,y) hold off title ('y[n]&x[n]') 